mapper.select:
        SELECT


--         级联表的字段
        ,meizhuang.shangjia_id as meizhuangShangjiaId
        ,meizhuang.meizhuang_uuid_number as meizhuangUuidNumber
        ,meizhuang.meizhuang_name as meizhuangName
        ,meizhuang.meizhuang_photo as meizhuangPhoto
        ,meizhuang.meizhuang_pinpai as meizhuangPinpai
        ,meizhuang.meizhuang_types as meizhuangTypes
        ,meizhuang.meizhuang_erji_types as meizhuangErjiTypes
        ,meizhuang.meizhuang_price as meizhuangPrice
        ,meizhuang.meizhuang_kucun_number as meizhuangKucunNumber
        ,meizhuang.meizhuang_old_money as meizhuangOldMoney
        ,meizhuang.meizhuang_new_money as meizhuangNewMoney
        ,meizhuang.meizhuang_clicknum as meizhuangClicknum
        ,meizhuang.meizhuang_yesno_types as meizhuangYesnoTypes
        ,meizhuang.meizhuang_content as meizhuangContent
        ,meizhuang.shangxia_types as shangxiaTypes
        ,meizhuang.meizhuang_delete as meizhuangDelete
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.new_money as newMoney
        ,yonghu.yonghu_sum_jifen as yonghuSumJifen
        ,yonghu.yonghu_new_jifen as yonghuNewJifen
        ,yonghu.huiyuandengji_types as huiyuandengjiTypes

        FROM meizhuang_commentback  a
        left JOIN meizhuang meizhuang ON a.meizhuang_id = meizhuang.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and (
                    a.meizhuang_id = #{params.meizhuangId}
                )


                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                and a.meizhuang_commentback_text like CONCAT('%',#{params.meizhuangCommentbackText},'%')


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


                and a.reply_text like CONCAT('%',#{params.replyText},'%')


                  and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


                  and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 




                and a.meizhuang_id IS NOT NULL


                and meizhuang.shangjia_id = #{params.shangjiaId}


                and meizhuang.meizhuang_uuid_number like CONCAT('%',#{params.meizhuangUuidNumber},'%')


                and meizhuang.meizhuang_name like CONCAT('%',#{params.meizhuangName},'%')


                and meizhuang.meizhuang_pinpai like CONCAT('%',#{params.meizhuangPinpai},'%')


                and meizhuang.meizhuang_types = #{params.meizhuangTypes}



                and meizhuang.meizhuang_erji_types = #{params.meizhuangErjiTypes}



                  and meizhuang.meizhuang_price >= #{params.meizhuangPriceStart}   


                  and meizhuang.meizhuang_price <= #{params.meizhuangPriceEnd}   


                and meizhuang.meizhuang_price = #{params.meizhuangPrice}


                  and meizhuang.meizhuang_kucun_number >= #{params.meizhuangKucunNumberStart}   


                  and meizhuang.meizhuang_kucun_number <= #{params.meizhuangKucunNumberEnd}   


                and meizhuang.meizhuang_kucun_number = #{params.meizhuangKucunNumber}


                  and meizhuang.meizhuang_old_money >= #{params.meizhuangOldMoneyStart}   


                  and meizhuang.meizhuang_old_money <= #{params.meizhuangOldMoneyEnd}   


                  and meizhuang.meizhuang_new_money >= #{params.meizhuangNewMoneyStart}   


                  and meizhuang.meizhuang_new_money <= #{params.meizhuangNewMoneyEnd}   


                  and meizhuang.meizhuang_clicknum >= #{params.meizhuangClicknumStart}   


                  and meizhuang.meizhuang_clicknum <= #{params.meizhuangClicknumEnd}   


                and meizhuang.meizhuang_clicknum = #{params.meizhuangClicknum}


                and meizhuang.meizhuang_yesno_types = #{params.meizhuangYesnoTypes}



                and meizhuang.meizhuang_content like CONCAT('%',#{params.meizhuangContent},'%')


                and meizhuang.shangxia_types = #{params.shangxiaTypes}



                  and meizhuang.meizhuang_delete >= #{params.meizhuangDeleteStart}   


                  and meizhuang.meizhuang_delete <= #{params.meizhuangDeleteEnd}   


                and meizhuang.meizhuang_delete = #{params.meizhuangDelete}



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


                  and yonghu.new_money >= #{params.newMoneyStart}   


                  and yonghu.new_money <= #{params.newMoneyEnd}   


                  and yonghu.yonghu_sum_jifen >= #{params.yonghuSumJifenStart}   


                  and yonghu.yonghu_sum_jifen <= #{params.yonghuSumJifenEnd}   


                  and yonghu.yonghu_new_jifen >= #{params.yonghuNewJifenStart}   


                  and yonghu.yonghu_new_jifen <= #{params.yonghuNewJifenEnd}   


                and yonghu.huiyuandengji_types = #{params.huiyuandengjiTypes}




        order by a.${params.orderBy} desc
